mapper.select:
select * from FESTIVAL
where F_NO between ${rowStart} and ${rowEnd}
order by F_NO desc

mapper.delete:
delete REVIEW 
where r_NO = #{r_NO}
	and u_EMAIL = #{u_EMAIL}

mapper.select:
select u_EMAIL
from REVIEW
	where r_NO = #{r_NO}

mapper.update:
update REVIEW 
set 
	r_CONTENT = #{r_CONTENT}
	where r_NO = #{r_NO}
		and u_EMAIL = #{u_EMAIL}
